mapper.select:
select out_trade_no
from user_group_buy_order_detail
where team_id = #{teamId}

select ugbod.user_id, ugbod.team_id, ugbod.order_id, ugbod.activity_id, ugbod.start_time, ugbod.end_time, ugbod.goods_id, ugbod.source, ugbod.channel, ugbod.original_price, ugbod.pay_price, ugbod.discount_deduction, ugbod.status, ugbod.out_trade_no, ugbod.out_trade_time, ugbod.biz_id from user_group_buy_order_detail ugbod join group_buy_order gbo on ugbod.team_id = gbo.team_id where ugbod.source = #{source} and ugbod.channel = #{channel} and ugbod.goods_id = #{goodsId} and ugbod.user_id = #{userId}
and now() < gbo.valid_end_time
limit #{count}

select ugbod.user_id, ugbod.team_id, ugbod.order_id, ugbod.activity_id, ugbod.start_time, ugbod.end_time, ugbod.goods_id, ugbod.source, ugbod.channel, ugbod.original_price, ugbod.pay_price, ugbod.discount_deduction, ugbod.status, ugbod.out_trade_no, ugbod.out_trade_time, ugbod.biz_id from user_group_buy_order_detail ugbod join group_buy_order gbo on ugbod.team_id = gbo.team_id where ugbod.source = #{source} and ugbod.channel = #{channel} and ugbod.goods_id = #{goodsId} and ugbod.user_id != #{userId}
and now() < gbo.valid_end_time
limit #{count}
